mapper.update:
UPDATE PRODUCT
SET PRODUCT_VIEW = PRODUCT_VIEW + 1
WHERE PRODUCT_NO = #{productNo}

mapper.select:
SELECT COUNT(*)
FROM SCRAP_PRODUCT
WHERE 
	PRODUCT_NO = #{productNo}
	AND MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO SCRAP_PRODUCT (
	PRODUCT_NO,
	MEMBER_NO
) VALUES (
	#{productNo},
	#{memberNo}
)

mapper.update:
UPDATE PRODUCT
SET SCRAP_COUNT = SCRAP_COUNT + 1
WHERE PRODUCT_NO = #{productNo}

UPDATE PRODUCT
SET SCRAP_COUNT = SCRAP_COUNT - 1
WHERE PRODUCT_NO = #{productNo}

mapper.select:
select member_no
from qna
where qna_no = #{refQnaNo}
