mapper.select:
SELECT COUNT(*)
FROM like_recipe
WHERE STATUS ='Y'
AND member_no =#{memberNo}
AND rcp_no =#{recipeNo}

SELECT COUNT(*)
FROM scrap_recipe
WHERE STATUS ='Y'
AND member_no =#{memberNo}
AND rcp_no =#{recipeNo}

INSERT INTO DIVISION (
	div_name,
	rcp_no
) VALUES(
	#{divName},
	#{recipeNo}
) 	
RETURNING *

INSERT INTO COOK_ORDER (
	cook_ord_content,
	cook_ord_photo,
	rcp_no

) VALUES(
	#{cookOrdContent},
	#{cookOrdPhoto},
	#{recipeNo}
) 	
RETURNING *

mapper.insert:
INSERT INTO COOK_TIP(
	cook_tip_content,
	cook_ord_no
) VALUES(
	#{cookTipContent},
	#{cookOrdNo}
)

mapper.update:
  		UPDATE RECIPE
		SET rcp_view = rcp_view + 1
		WHERE rcp_no = #{recipeNo}

mapper.insert:
INSERT INTO scrap_recipe(
member_no,
rcp_no
)VALUES(
	#{memberNo, jdbcType=INTEGER},
	#{recipeNo}
)

INSERT INTO like_recipe(
member_no,
rcp_no
) VALUES(
#{memberNo},
#{recipeNo}
)

mapper.delete:
DELETE FROM DIVISION
WHERE div_no = #{divNo}
AND rcp_no = #{recipeNo};

DELETE FROM COOK_ORDER
WHERE rcp_no=#{recipeNo}
AND cook_ord_no =#{cookOrdNo};

	 DELETE FROM scrap_recipe
    WHERE rcp_no = #{recipeNo}
	AND member_no =#{memberNo}
	AND status ='Y'

	 DELETE FROM like_recipe
    WHERE rcp_no = #{recipeNo}
	AND member_no =#{memberNo}
	AND status ='Y'
